import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Container,
  Typography,
  Paper,
  TextField,
  Button,
  CircularProgress,
  Alert,
  useMediaQuery,
  useTheme,
} from '@mui/material';

export default function HelpSharingAgent() {
  const [message, setMessage] = useState('');
  const [dateTime, setDateTime] = useState('');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState<{ severity: 'success' | 'error'; message: string } | null>(
    { severity: 'success', message: 'Authentication successful! You are now logged in as the help agent.' }
  );
  const [requests, setRequests] = useState<Array<{
    id: string;
    message: string;
    dateTime: string;
  }>>([]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  async function submitHelpRequest() {
    if (!message.trim() || !dateTime) {
      setAlert({ severity: 'error', message: 'Please enter a message and select a date/time' });
      return;
    }
    
    setLoading(true);
    setAlert(null);
    
    try {
      const response = await axios.post('http://localhost:3001/api/help-requests', {
        message: message.trim(),
        dateTime: new Date(dateTime).toISOString(),
        email: 'agent@neighborhoodhelp.com',
        name: 'Neighborhood Help Agent'
      });
      
      setAlert({
        severity: 'success',
        message: 'Help request submitted successfully!',
      });
      
      // Clear form
      setMessage('');
      setDateTime('');
      
      // Refresh requests
      fetchHelpRequests();
    } catch (err: any) {
      setAlert({ 
        severity: 'error', 
        message: err.response?.data?.error || 'Failed to submit help request. Please try again.' 
      });
      console.error(err);
    } finally {
      setLoading(false);
    }
  }
  
  async function fetchHelpRequests() {
    try {
      const response = await axios.get('http://localhost:3001/api/help-requests');
      setRequests(response.data);
    } catch (err) {
      console.error('Failed to fetch help requests:', err);
    }
  }
  
  // Load requests on component mount
  React.useEffect(() => {
    fetchHelpRequests();
  }, []);

  return (
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #83a4d4, #b6fbff)',
          p: 2,
        }}
      >
        {/* Hero Section */}
        <Box 
          sx={{
            height: '300px',
            width: '100%',
            backgroundImage: 'url(https://images.unsplash.com/photo-1600880292203-757bb62b4baf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            borderRadius: 2,
            mb: 4,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'relative',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0, 0, 0, 0.4)',
              borderRadius: 2,
            },
          }}
        >
          <Box sx={{ position: 'relative', zIndex: 1, textAlign: 'center', color: 'white', p: 3 }}>
            <Typography variant="h2" component="h1" fontWeight="bold" gutterBottom>
              Neighborhood Help Network
            </Typography>
            <Typography variant="h5" component="p">
              Connecting Communities Through Kindness and Support
            </Typography>
          </Box>
        </Box>

        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Container maxWidth="md">
          <Paper
            elevation={10}
            sx={{
              display: 'flex',
              flexDirection: isMobile ? 'column' : 'row',
              borderRadius: 4,
              overflow: 'hidden',
              boxShadow: '0 15px 35px rgba(50,50,93,.1), 0 5px 15px rgba(0,0,0,.07)',
            }}
          >
            {/* Left Side - Form */}
            <Box sx={{ flex: 1, p: 5, backgroundColor: 'white' }}>
              <Typography variant="h3" color="primary" fontWeight="bold" gutterBottom>
                Neighborhood Help Agent
              </Typography>
              <Typography variant="subtitle1" color="textSecondary" mb={4}>
                Welcome back! You're logged in as the neighborhood help agent.
              </Typography>
              
              <Box
                sx={{
                  mt: 2,
                  mb: 3,
                  p: 2,
                  bgcolor: '#e6f7e6',
                  borderRadius: 1,
                  border: '1px solid #4caf50',
                  color: '#1b5e20',
                  fontSize: 14,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1
                }}
              >
                <span style={{ fontSize: '1.2rem' }}>âœ“</span>
                <span>Authentication successful! You are now logged in as the help agent.</span>
              </Box>

              {alert && (
                <Alert severity={alert.severity} onClose={() => setAlert(null)} sx={{ mb: 3 }}>
                  {alert.message}
                </Alert>
              )}


              <TextField
                label="How can we help?"
                multiline
                rows={3}
                fullWidth
                variant="outlined"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                sx={{ mb: 2 }}
                placeholder="Example: Need help with grocery shopping or a ride to the doctor"
              />

              <TextField
                label="Preferred Date & Time"
                type="datetime-local"
                fullWidth
                variant="outlined"
                value={dateTime}
                onChange={(e) => setDateTime(e.target.value)}
                InputLabelProps={{ shrink: true }}
                sx={{ mb: 3 }}
              />

              <Button
                variant="contained"
                color="primary"
                size="large"
                fullWidth
                disabled={loading}
                onClick={submitHelpRequest}
                sx={{ fontWeight: 'bold', py: 1.5 }}
                startIcon={loading ? <CircularProgress size={20} color="inherit" /> : null}
              >
                {loading ? 'Submitting...' : 'Submit Help Request'}
              </Button>

              {requests.length > 0 && (
                <Box sx={{ mt: 4 }}>
                  <Typography variant="h6" gutterBottom>
                    Recent Help Requests
                  </Typography>
                  <Box sx={{ maxHeight: 300, overflowY: 'auto', pr: 1 }}>
                    {requests.map((request) => (
                      <Paper key={request.id} sx={{ p: 2, mb: 2, bgcolor: '#f8f9fa' }}>
                        <Typography variant="subtitle2" color="text.secondary">
                          {new Date(request.dateTime).toLocaleString()}
                        </Typography>
                        <Typography variant="h6" sx={{ mt: 0.5, mb: 1 }}>
                          Help request:
                        </Typography>
                        <Typography paragraph sx={{ mb: 1, whiteSpace: 'pre-wrap' }}>
                          {request.message}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {new Date(request.dateTime).toLocaleString()}
                        </Typography>
                      </Paper>
                    ))}
                  </Box>
                </Box>
              )}
            </Box>

            {/* Right Side - Info */}
            <Box
              sx={{
                flex: 1,
                p: 4,
                bgcolor: 'white',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                borderRadius: 2,
                boxShadow: 1,
                border: '1px solid #e0e0e0',
              }}
            >
              <Typography variant="h5" color="primary" fontWeight="bold" gutterBottom sx={{ color: '#2c3e50' }}>
                How It Works
              </Typography>
              <Box component="ul" sx={{ pl: 3, '& li': { mb: 1.5 } }}>
                <li>Fill out the form to request help or offer assistance</li>
                <li>Your request will be visible to neighbors who can help</li>
                <li>Connect directly via email to coordinate details</li>
                <li>Help build a stronger, more supportive community</li>
              </Box>
              
              <Box sx={{ mt: 4 }}>
                <Typography variant="h6" color="primary" fontWeight="bold" gutterBottom sx={{ color: '#2c3e50', mt: 3 }}>
                  Need Help Right Away?
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Call our community hotline: +91 9876543210
               </Typography>
              </Box>
            </Box>
          </Paper>
        </Container>
      </Box>
      </Box>
  );
}
