import React, { useState } from 'react';
import axios from 'axios';

// Stub auth provider
const AuthProvider: React.FC<{ projectId: string; children?: React.ReactNode }> = ({ children }) => <>{children}</>;

// Stub Descope login component
const Descope: React.FC<{
  flowId: string;
  theme?: string;
  onSuccess?: (e: any) => void;
  onError?: (e: any) => void;
}> = ({ onSuccess }) => {
  // Simple mocked sign-in button for demo
  return (
    <div style={{ padding: 20, border: '1px solid #ccc', borderRadius: 8 }}>
      <p>Login (stub)</p>
      <button
        onClick={() => {
          const fakeEvent = {
            detail: {
              user: { email: 'testuser@example.com', name: 'Test User' },
              session: { token: 'fake-session-token' },
            },
          };
          onSuccess && onSuccess(fakeEvent);
        }}
      >
        Sign In
      </button>
    </div>
  );
};

export default function HelpSharingAgent() {
  const [user, setUser] = useState<{ email: string; name?: string } | null>(null);
  const [sessionToken, setSessionToken] = useState<string | null>(null);
  const [message, setMessage] = useState('');
  const [dateTime, setDateTime] = useState('');
  const [loading, setLoading] = useState(false);

  async function createEvent() {
    if (!user || !sessionToken) {
      alert('Please log in first');
      return;
    }
    if (!message.trim() || !dateTime) {
      alert('Please enter a message and date/time');
      return;
    }

    setLoading(true);

    try {
      const event = {
        summary: 'Neighborhood Help Request',
        description: message.trim(),
        start: {
          dateTime: new Date(dateTime).toISOString(),
          timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
        },
        end: {
          dateTime: new Date(new Date(dateTime).getTime() + 30 * 60000).toISOString(),
          timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
        },
        reminders: { useDefault: true },
      };

      const response = await axios.post(
        'http://localhost:3001/api/create-event',
        { event, email: user.email },
        { headers: { Authorization: `Bearer ${sessionToken}` } }
      );

      alert(`Event created: ${response.data.summary} at ${new Date(response.data.start.dateTime).toLocaleString()}`);
      setMessage('');
      setDateTime('');
    } catch (err) {
      alert('Failed to create event. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  return (
    <AuthProvider projectId="P321NcYgY3z0ACnbCyQyA3rWmY3V">
      <div
        style={{
          maxWidth: 500,
          margin: '40px auto',
          padding: 20,
          borderRadius: 10,
          boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        }}
      >
        <div style={{ textAlign: 'center', marginBottom: 20 }}>
          <img src="/logo192.png" alt="Logo" style={{ height: 60, marginBottom: 10 }} />
          <h1 style={{ margin: 0, fontWeight: 700, color: '#1473e6' }}>Neighborhood Help Coordinator</h1>
          <p style={{ color: '#666' }}>Connect with neighbors & share support easily</p>
        </div>

        {!user ? (
          <Descope
            flowId="help-agent-flow"
            theme="light"
            onSuccess={(e: any) => {
              setUser(e.detail.user);
              setSessionToken(e.detail.session.token);
            }}
            onError={(err: any) => {
              alert('Authentication error.');
              console.error(err);
            }}
          />
        ) : (
          <>
            <div style={{ marginBottom: 20, color: '#333' }}>
              Welcome, <strong>{user.name || user.email}</strong>! You’re signed in.
            </div>

            <textarea
              placeholder="Describe your help request or offer..."
              value={message}
              onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setMessage(e.target.value)}
              rows={4}
              style={{
                width: '100%',
                padding: 10,
                fontSize: 16,
                borderRadius: 6,
                border: '1px solid #ccc',
                resize: 'vertical',
                marginBottom: 15,
                fontFamily: 'inherit',
              }}
            />

            <input
              type="datetime-local"
              value={dateTime}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) => setDateTime(e.target.value)}
              style={{
                width: '100%',
                padding: 10,
                fontSize: 16,
                borderRadius: 6,
                border: '1px solid #ccc',
                marginBottom: 20,
                fontFamily: 'inherit',
              }}
            />

            <button
              onClick={createEvent}
              disabled={loading}
              style={{
                width: '100%',
                backgroundColor: '#1473e6',
                color: 'white',
                padding: '12px 0',
                fontSize: 18,
                fontWeight: 'bold',
                border: 'none',
                borderRadius: 6,
                cursor: loading ? 'not-allowed' : 'pointer',
              }}
            >
              {loading ? 'Creating Event...' : 'Create Help Event'}
            </button>

            <button
              onClick={() => {
                setUser(null);
                setSessionToken(null);
                setMessage('');
                setDateTime('');
              }}
              style={{
                width: '100%',
                marginTop: 15,
                backgroundColor: '#f44336',
                color: 'white',
                padding: '10px 0',
                fontSize: 16,
                borderRadius: 6,
                border: 'none',
                cursor: 'pointer',
              }}
            >
              Sign out
            </button>
          </>
        )}
      </div>
    </AuthProvider>
  );
}
