import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Container,
  Typography,
  Paper,
  TextField,
  Button,
  CircularProgress,
  Alert,
  useMediaQuery,
  useTheme,
} from '@mui/material';

export default function HelpSharingAgent() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [dateTime, setDateTime] = useState('');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState<{ severity: 'success' | 'error'; message: string } | null>(null);
  const [requests, setRequests] = useState<Array<{
    id: string;
    name: string;
    email: string;
    message: string;
    dateTime: string;
  }>>([]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  async function submitHelpRequest() {
    if (!name.trim() || !email.trim() || !message.trim() || !dateTime) {
      setAlert({ severity: 'error', message: 'Please fill in all fields' });
      return;
    }
    
    setLoading(true);
    setAlert(null);
    
    try {
      const response = await axios.post('http://localhost:3001/api/help-requests', {
        name: name.trim(),
        email: email.trim(),
        message: message.trim(),
        dateTime: new Date(dateTime).toISOString()
      });
      
      setAlert({
        severity: 'success',
        message: 'Help request submitted successfully!',
      });
      
      // Clear form
      setName('');
      setEmail('');
      setMessage('');
      setDateTime('');
      
      // Refresh requests
      fetchHelpRequests();
    } catch (err: any) {
      setAlert({ 
        severity: 'error', 
        message: err.response?.data?.error || 'Failed to submit help request. Please try again.' 
      });
      console.error(err);
    } finally {
      setLoading(false);
    }
  }
  
  async function fetchHelpRequests() {
    try {
      const response = await axios.get('http://localhost:3001/api/help-requests');
      setRequests(response.data);
    } catch (err) {
      console.error('Failed to fetch help requests:', err);
    }
  }
  
  // Load requests on component mount
  React.useEffect(() => {
    fetchHelpRequests();
  }, []);

  return (
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #83a4d4, #b6fbff)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          p: 2,
        }}
      >
        <Container maxWidth="md">
          <Paper
            elevation={10}
            sx={{
              display: 'flex',
              flexDirection: isMobile ? 'column' : 'row',
              borderRadius: 4,
              overflow: 'hidden',
              boxShadow: '0 15px 35px rgba(50,50,93,.1), 0 5px 15px rgba(0,0,0,.07)',
            }}
          >
            {/* Left Side - Form */}
            <Box sx={{ flex: 1, p: 5, backgroundColor: 'white' }}>
              <Typography variant="h3" color="primary" fontWeight="bold" gutterBottom>
                Neighborhood Help Coordinator
              </Typography>
              <Typography variant="subtitle1" color="textSecondary" mb={4}>
                Connect with neighbors & share support easily. Post requests or offers to help around your community.
              </Typography>

              {alert && (
                <Alert severity={alert.severity} onClose={() => setAlert(null)} sx={{ mb: 3 }}>
                  {alert.message}
                </Alert>
              )}

              <TextField
                label="Your Name"
                fullWidth
                variant="outlined"
                value={name}
                onChange={(e) => setName(e.target.value)}
                sx={{ mb: 2 }}
                placeholder="John Doe"
              />

              <TextField
                label="Your Email"
                type="email"
                fullWidth
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                sx={{ mb: 2 }}
                placeholder="john@example.com"
              />

              <TextField
                label="How can we help?"
                multiline
                rows={3}
                fullWidth
                variant="outlined"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                sx={{ mb: 2 }}
                placeholder="Example: Need help with grocery shopping or a ride to the doctor"
              />

              <TextField
                label="Preferred Date & Time"
                type="datetime-local"
                fullWidth
                variant="outlined"
                value={dateTime}
                onChange={(e) => setDateTime(e.target.value)}
                InputLabelProps={{ shrink: true }}
                sx={{ mb: 3 }}
              />

              <Button
                variant="contained"
                color="primary"
                size="large"
                fullWidth
                disabled={loading}
                onClick={submitHelpRequest}
                sx={{ fontWeight: 'bold', py: 1.5 }}
                startIcon={loading ? <CircularProgress size={20} color="inherit" /> : null}
              >
                {loading ? 'Submitting...' : 'Submit Help Request'}
              </Button>

              {requests.length > 0 && (
                <Box sx={{ mt: 4 }}>
                  <Typography variant="h6" gutterBottom>
                    Recent Help Requests
                  </Typography>
                  <Box sx={{ maxHeight: 300, overflowY: 'auto', pr: 1 }}>
                    {requests.map((request) => (
                      <Paper key={request.id} sx={{ p: 2, mb: 2, bgcolor: '#f8f9fa' }}>
                        <Typography variant="subtitle2" color="text.secondary">
                          {new Date(request.dateTime).toLocaleString()}
                        </Typography>
                        <Typography variant="h6" sx={{ mt: 0.5 }}>
                          {request.name} needs help with:
                        </Typography>
                        <Typography paragraph sx={{ mb: 1 }}>
                          {request.message}
                        </Typography>
                        <Typography variant="body2" color="primary">
                          Contact: {request.email}
                        </Typography>
                      </Paper>
                    ))}
                  </Box>
                </Box>
              )}
            </Box>

            {/* Right Side - Info */}
            <Box
              sx={{
                flex: 1,
                p: 4,
                bgcolor: '#f5f9ff',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
              }}
            >
              <Typography variant="h5" color="primary" fontWeight="bold" gutterBottom>
                How It Works
              </Typography>
              <Box component="ul" sx={{ pl: 3, '& li': { mb: 1.5 } }}>
                <li>Fill out the form to request help or offer assistance</li>
                <li>Your request will be visible to neighbors who can help</li>
                <li>Connect directly via email to coordinate details</li>
                <li>Help build a stronger, more supportive community</li>
              </Box>
              
              <Box sx={{ mt: 4 }}>
                <Typography variant="h6" color="primary" fontWeight="bold" gutterBottom>
                  Need Help Right Away?
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Call our community hotline: (555) 123-4567
                </Typography>
              </Box>
            </Box>
          </Paper>
        </Container>
      </Box>
  );
}
